(function() {
  var CsvWriterMixin, sinon;

  require('should');

  sinon = require('sinon');

  CsvWriterMixin = require('../CsvWriterMixin');

  describe('CsvWriterMixin(options)', function() {
    return it('should require path to specified in options', function() {
      return (function() {
        return CsvWriterMixin({});
      }).should["throw"]('path option is required.');
    });
  });

}).call(this);
